document.querySelectorAll(".displayDetail").forEach(function(a){return a.addEventListener("click",function(){return toggleDetail(a.getAttribute("data-id"))})});function toggleDetail(a){a=document.getElementById("box-"+a);a.classList.contains("noDetail")?(a.classList.remove("noDetail"),a.classList.add("withDetail")):(a.classList.remove("withDetail"),a.classList.add("noDetail"))};
